import './Expenses.css'
import ExpenseItem from "./ExpenseItem";
import Card from '../UI/Card'
import ExpensesFilter from './ExpensesFilter';
import {useState} from 'react'


const Expenses = props =>{
    const [toBeFiltered, setToBeFiltered] = useState('')


    return (
    <Card className = "expenses">
        <ExpensesFilter filter = {setToBeFiltered} expenses={props.expenses}/>
        {toBeFiltered===''?props.expenses.map( e => <ExpenseItem key={e.id} element = {e}/>):
        props.expenses.filter(e => {e === toBeFiltered})
        }
    </Card>
    )
}

export default Expenses
