  // );

  const addExpenseHandler = (newItem) => {
    setExpenses({
      ...expenses,
      newItem}
    )
    console.log(expenses)
  }

  return (
    <div>
      <NewExpense onAddExpense={addExpenseHandler} expenses={expenses} />
      <Expenses expenses={expenses} />
