import './Expenses.css'
import ExpenseItem from "./ExpenseItem";
import Card from '../UI/Card'
import ExpensesFilter from './ExpensesFilter';
import {useState} from 'react'


const Expenses = props =>{
    const [toBeFiltered, setToBeFiltered] = useState('')

    const filterElementsHandler = e => {
        setToBeFiltered(e)
    }

    return (
    <Card className = "expenses">
        <ExpensesFilter filter = {filterElementsHandler} expenses={props.expenses}/>
        {toBeFiltered===''?props.expenses.map( e => <ExpenseItem key={e.id} element = {e}/>):
        props.expenses.map(e => {e.date.getFullYear() === toBeFiltered &&
            <ExpenseItem key={e.id} element = {e}/>
        })
        }
    </Card>
    )
}

export default Expenses
